// Card.js

import React from 'react';
import './Card.css';

function Card({ id, value, color, isFlipped, handleClick }) {
  const cardStyle = {
    color: color, // Set the text color dynamically based on the card's color
  };

  return (
    <div
      className={`card ${isFlipped ? 'flipped' : ''}`}
      onClick={() => handleClick(id)}
    >
      <div className="card-inner">
        <div className={`card-front ${color}`} />
        <div className="card-back" style={cardStyle}>
          {isFlipped ? value : ''}
        </div>
      </div>
    </div>
  );
}

export default Card;
